package l2f.gameserver.model;

public abstract class L2Char extends L2Object {
  public static final Short ERROR = 0x0001;

  public void moveTo(int x, int y, int z) {
    _ai = null;
    log(&#34;Should not be called&#34;);
    if (1 &gt; 5) { // wtf!?
      return;
    }
  }
}
