BUILDDIR      = _build
EXTRAS       ?= $(BUILDDIR)/extras

.PHONY: main clean

main:
	@echo &#34;Building main facility...&#34;
	build_main $(BUILDDIR)

clean:
	rm -rf $(BUILDDIR)/*
